import * as React from 'react';
import { Box, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { PunctuationCharacter } from '../components/PunctuationCharacter';
import { GameOverDialog } from '../components/GameOverDialog';
import { PUNCTUATION_TYPES, PHRASES, PunctuationType, Phrase } from '../constants';
import { Button as CustomButton } from '../components/Button';

const GameScreen = () => {
  // ... existing state and functions

  return (
    <Box
      sx={{
        height: '100vh',
        display: 'flex',
        flexDirection: 'column',
        overflow: 'hidden',
        bgcolor: '#F5F5F7'
      }}
    >
      {/* Sticky Header */}
      <Box
        sx={{
          position: 'sticky',
          top: 0,
          padding: 2,
          backgroundColor: 'white',
          zIndex: 10,
          borderBottom: '1px solid rgba(0,0,0,0.1)',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}
      >
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <CustomButton onClick={() => navigate('/select')}>
            Back
          </CustomButton>
          {timeLeft !== null && (
            <Typography variant="h6">
              Time: {timeLeft}s
            </Typography>
          )}
        </Box>
        <Typography variant="h6">
          Score: {score.correct}/{score.correct + score.incorrect}
        </Typography>
      </Box>

      {/* Main Content Area */}
      <Box
        sx={{
          flex: 1,
          overflow: 'auto',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          padding: 2
        }}
      >
        {renderPhrase()}
      </Box>

      {/* Sticky Footer */}
      <Box
        sx={{
          position: 'sticky',
          bottom: 0,
          padding: 2,
          backgroundColor: 'white',
          zIndex: 10,
          borderTop: '1px solid rgba(0,0,0,0.1)',
          display: 'flex',
          justifyContent: 'center',
          gap: 2
        }}
      >
        {PUNCTUATION_TYPES.map((type) => (
          <PunctuationCharacter
            key={type}
            type={type}
            onClick={() => handleCharacterSelect(type)}
            isDraggable={true}
            onDragStart={() => {}}
            onSelect={handleCharacterSelect}
            isCorrect={selectedMark === type ? isCorrect : undefined}
          />
        ))}
      </Box>

      <GameOverDialog
        open={gameOverOpen}
        onClose={handleGameOverClose}
        score={score}
      />
    </Box>
  );
};

export default GameScreen;
