import { createTheme } from '@mui/material/styles';

declare module '@mui/material/styles' {
  interface Palette {
    bone: {
      main: string;
    };
    characters: {
      peri: string;
      ex: string;
      quest: string;
      curly: string;
      pence: string;
    };
  }
  interface PaletteOptions {
    bone: {
      main: string;
    };
    characters: {
      peri: string;
      ex: string;
      quest: string;
      curly: string;
      pence: string;
    };
  }
}

export const theme = createTheme({
  typography: {
    fontFamily: '"Punx Grotesk", Arial, sans-serif',
    h1: {
      fontFamily: '"Rethink Sans", Arial, sans-serif',
      fontWeight: 800,
    },
    h2: {
      fontFamily: '"Rethink Sans", Arial, sans-serif',
      fontWeight: 800,
    },
    h3: {
      fontFamily: '"Rethink Sans", Arial, sans-serif',
      fontWeight: 800,
    },
    h4: {
      fontFamily: '"Rethink Sans", Arial, sans-serif',
      fontWeight: 800,
    },
    h5: {
      fontFamily: '"Rethink Sans", Arial, sans-serif',
      fontWeight: 800,
    },
    h6: {
      fontFamily: '"Rethink Sans", Arial, sans-serif',
      fontWeight: 800,
    },
    body1: {
      fontFamily: '"Punx Grotesk", Arial, sans-serif',
    },
    body2: {
      fontFamily: '"Punx Grotesk", Arial, sans-serif',
    },
    button: {
      fontFamily: '"Rethink Sans", Arial, sans-serif',
      fontWeight: 800,
      textTransform: 'none', // Prevents automatic uppercase transformation
    },
  },
  palette: {
    common: {
      black: '#000000',
      white: '#FFFFFF',
    },
    bone: {
      main: '#FFFEFA',
    },
    characters: {
      peri: '#E11D21',   // Red
      ex: '#07A9D2',     // Blue
      quest: '#008F40',  // Green
      curly: '#FAC500',  // Yellow
      pence: '#FDD91C',  // Yellow
    },
  },
  components: {
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          backgroundColor: '#FFFEFA', // Bone background color
        },
      },
    },
  },
});
