import * as React from 'react';
import { Box, Typography, Button, Grid, Dialog, DialogContent, DialogActions } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useGame } from '../context/GameContext';
import PunctuationCharacter from '../components/PunctuationCharacter';
import { shuffleArray } from '../utils/shuffle';
import { Button as CustomButton } from '../components/Button';

type Phrase = {
  text: string;
  answer: string;
  position: 'end' | number; // 'end' or index where punctuation should go
};

type PhraseCollection = {
  period: Phrase[];
  exclamation: Phrase[];
  question: Phrase[];
  comma: Phrase[];
};

const samplePhrases: PhraseCollection = {
  period: [
    { text: "I love to play in the park", answer: "period", position: 'end' },
    { text: "The sun is shining today", answer: "period", position: 'end' },
  ],
  exclamation: [
    { text: "What a wonderful day", answer: "exclamation", position: 'end' },
    { text: "I can’t believe it", answer: "exclamation", position: 'end' },
  ],
  question: [
    { text: "How are you today", answer: "question", position: 'end' },
    { text: "Where did you go", answer: "question", position: 'end' },
  ],
  comma: [
    { text: "After the movie we went home", answer: "comma", position: 2 }, // After index 2 (after "movie")
    { text: "Yes I would love to", answer: "comma", position: 0 }, // After index 1 (after "Yes")
  ],
};

type PunctuationType = 'period' | 'exclamation' | 'question' | 'comma';

const punctuationTypes: PunctuationType[] = ['period', 'exclamation', 'question', 'comma'];

interface FeedbackMessage {
  text: string;
  type: 'success' | 'error';
}

interface GameOverDialogProps {
  open: boolean;
  onClose: () => void;
  score: { correct: number; incorrect: number };
}

const GameOverDialog = ({ open, onClose, score }: GameOverDialogProps) => (
  <Dialog open={open} onClose={onClose}>
    <DialogContent>
      <Typography variant="h4" sx={{ mb: 2 }}>Time's Up!</Typography>
      <Typography variant="h6" sx={{ color: 'success.main', mb: 1 }}>
        Correct Answers: {score.correct}
      </Typography>
      <Typography variant="h6" sx={{ color: 'error.main', mb: 2 }}>
        Incorrect Answers: {score.incorrect}
      </Typography>
      <Typography variant="body1">
        {score.correct > score.incorrect 
          ? "Great job! You're getting really good at this!" 
          : "Keep practicing, you're getting better!"}
      </Typography>
    </DialogContent>
    <DialogActions>
      <Button onClick={onClose}>Play Again</Button>
    </DialogActions>
  </Dialog>
);

const GameScreen = () => {
  const navigate = useNavigate();
  const { punctuationType, settings } = useGame();
  const [currentPhraseIndex, setCurrentPhraseIndex] = React.useState(0);
  const [currentPhrase, setCurrentPhrase] = React.useState<Phrase | null>(null);
  const [selectedMark, setSelectedMark] = React.useState<PunctuationType | null>(null);
  const [isCorrect, setIsCorrect] = React.useState<boolean | null>(null);
  const [score, setScore] = React.useState({ correct: 0, incorrect: 0 });
  const [hasStarted, setHasStarted] = React.useState(false);
  const [timeLeft, setTimeLeft] = React.useState<number | null>(null);
  const [gameOverOpen, setGameOverOpen] = React.useState(false);
  const [shuffledTypes, setShuffledTypes] = React.useState<PunctuationType[]>(['period', 'exclamation', 'question', 'comma']);
  const [feedback, setFeedback] = React.useState<FeedbackMessage | null>(null);
  const timerRef = React.useRef<NodeJS.Timeout | null>(null);
  const [dragPosition, setDragPosition] = React.useState<{ x: number; y: number } | null>(null);
  const [activeDropZone, setActiveDropZone] = React.useState<number | 'end' | null>(null);
  const phraseRef = React.useRef<HTMLDivElement>(null);

  const phrases = React.useMemo(() => shuffleArray(Object.values(samplePhrases).flat()), []);

  React.useEffect(() => {
    setCurrentPhrase(phrases[currentPhraseIndex]);
  }, [currentPhraseIndex, phrases]);

  const startTimer = React.useCallback(() => {
    if (!hasStarted && settings.timer) {
      setHasStarted(true);
      setTimeLeft(settings.timeLimit);
    }
  }, [hasStarted, settings.timer, settings.timeLimit]);

  React.useEffect(() => {
    if (hasStarted && settings.timer && timeLeft !== null && timeLeft > 0) {
      timerRef.current = setInterval(() => {
        setTimeLeft((prev) => {
          if (prev === null || prev <= 1) {
            if (timerRef.current) {
              clearInterval(timerRef.current);
            }
            setGameOverOpen(true);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);

      return () => {
        if (timerRef.current) {
          clearInterval(timerRef.current);
        }
      };
    }
  }, [hasStarted, settings.timer, timeLeft]);

  React.useEffect(() => {
    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, []);

  const handleFirstInteraction = () => {
    startTimer();
  };

  const getFeedbackMessage = (type: string, isCorrect: boolean): FeedbackMessage => {
    if (isCorrect) {
      return {
        text: "Great job! That's exactly right!",
        type: 'success'
      };
    }

    const correctType = currentPhrase?.answer;
    switch (correctType) {
      case 'period':
        return {
          text: "This sentence needs a period to show it's complete.",
          type: 'error'
        };
      case 'question':
        return {
          text: "This is a question, so it needs a question mark.",
          type: 'error'
        };
      case 'exclamation':
        return {
          text: "This sentence shows strong feeling or emotion, so it needs an exclamation mark!",
          type: 'error'
        };
      case 'comma':
        return {
          text: "A comma is needed here to separate parts of the sentence.",
          type: 'error'
        };
      default:
        return {
          text: "That's not quite right. Try again!",
          type: 'error'
        };
    }
  };

  const handleCharacterSelect = (type: PunctuationType) => {
    if (!currentPhrase) return;

    // Start timer on first interaction if not started
    if (!hasStarted) {
      startTimer();
    }

    setSelectedMark(type);
    const isCorrectMark = type === currentPhrase.answer;
    setIsCorrect(isCorrectMark);

    if (isCorrectMark) {
      setScore(prev => ({
        ...prev,
        correct: prev.correct + 1
      }));
    } else {
      setScore(prev => ({
        ...prev,
        incorrect: prev.incorrect + 1
      }));
    }

    // Wait for animation then move to next phrase
    setTimeout(() => {
      setSelectedMark(null);
      setIsCorrect(null);
      goToNextPhrase();
    }, 1000);
  };

  const goToNextPhrase = () => {
    const nextIndex = (currentPhraseIndex + 1) % phrases.length;
    setCurrentPhraseIndex(nextIndex);
  };

  const handleDrop = (e: React.DragEvent<HTMLElement>) => {
    e.preventDefault();

    const droppedType = e.dataTransfer.getData('application/punctuation') as PunctuationType;

    if (activeDropZone !== null && droppedType) {
      const isCorrectPosition = 
        (currentPhrase?.position === 'end' && activeDropZone === 'end') ||
        (currentPhrase?.position === activeDropZone);

      setSelectedMark(droppedType);
      setIsCorrect(isCorrectPosition);

      if (isCorrectPosition) {
        setScore(prev => ({
          ...prev,
          correct: prev.correct + 1
        }));
      } else {
        setScore(prev => ({
          ...prev,
          incorrect: prev.incorrect + 1
        }));
      }

      // Keep the drop zone and feedback visible for 1 second
      setTimeout(() => {
        setSelectedMark(null);
        setIsCorrect(null);
        setActiveDropZone(null);
        goToNextPhrase();
      }, 1000);
    }
  };

  const handleDragOver = (e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault(); // This is crucial for enabling drop
    e.currentTarget.style.backgroundColor = 'rgba(0, 0, 0, 0.1)';
  };

  const handleDragLeave = (e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    e.currentTarget.style.backgroundColor = 'transparent';
  };

  const handleDragStart = (e: React.DragEvent) => {
    startTimer();
    e.dataTransfer.setData('text/plain', e.currentTarget.id);
  };

  const calculateDropZone = (dragX: number, dragY: number) => {
    if (!phraseRef.current) return null;

    const phraseRect = phraseRef.current.getBoundingClientRect();
    const words = phraseRef.current.getElementsByClassName('word');

    // Check if we're near the end of the phrase
    const lastWord = words[words.length - 1];
    const lastWordRect = lastWord.getBoundingClientRect();

    if (dragX > lastWordRect.right - 20) {
      return 'end';
    }

    // Check positions between words
    for (let i = 0; i < words.length - 1; i++) {
      const word = words[i];
      const nextWord = words[i + 1];
      const wordRect = word.getBoundingClientRect();
      const nextWordRect = nextWord.getBoundingClientRect();
      const gapCenter = (wordRect.right + nextWordRect.left) / 2;

      if (Math.abs(dragX - gapCenter) < 40) {
        return i;
      }
    }

    return null;
  };

  const renderPhrase = () => {
    if (!currentPhrase) return null;

    const words = currentPhrase.text.split(' ');

    return (
      <div 
        ref={phraseRef}
        style={{ 
          width: '100%',
          textAlign: 'center',
          willChange: 'transform',
          transform: 'translateZ(0)',
          backfaceVisibility: 'hidden'
        }}
      >
        <div style={{
          fontFamily: '"Bookman Old Style Regular", "Bookman", "URW Bookman L", serif',
          fontWeight: 'normal',
          fontSize: window.innerWidth < 600 ? '80px' : '120px',
          lineHeight: '1.1',
          display: 'inline-block',
          textAlign: 'left',
          maxWidth: '90%',
          margin: '0 auto',
        }}>
          {words.map((word, index) => (
            <React.Fragment key={index}>
              <span 
                className="word"
                style={{ 
                  display: 'inline-block',
                  transition: 'margin 0.3s ease',
                  marginRight: activeDropZone === index ? '140px' : '16px'
                }}
              >
                {word}
              </span>
              {activeDropZone === index && (
                <span
                  className="dropzone"
                  data-dropzone="true"
                  onDragEnter={(e) => {
                    e.preventDefault();
                    e.currentTarget.style.backgroundColor = 'rgba(0, 0, 0, 0.1)';
                  }}
                  onDragOver={(e) => {
                    e.preventDefault();
                    e.currentTarget.style.border = '6px dashed #666';
                  }}
                  onDragLeave={(e) => {
                    e.preventDefault();
                    e.currentTarget.style.border = '6px dashed #ccc';
                    e.currentTarget.style.backgroundColor = 'transparent';
                  }}
                  onDrop={handleDrop}
                  style={{
                    display: 'inline-flex',
                    width: '140px',
                    height: '140px',
                    border: '6px dashed #ccc',
                    borderRadius: '20px',
                    position: 'absolute',
                    transform: 'translateX(-150px)',
                    justifyContent: 'center',
                    alignItems: 'center',
                    transition: 'all 0.2s ease',
                    opacity: 0.8
                  }}
                >
                  {selectedMark && activeDropZone === index && (
                    <PunctuationCharacter
                      type={selectedMark}
                      isCorrect={isCorrect}
                      onClick={() => {}}
                    />
                  )}
                </span>
              )}
            </React.Fragment>
          ))}
          {activeDropZone === 'end' && (
            <span
              className="dropzone"
              data-dropzone="true"
              onDragEnter={(e) => {
                e.preventDefault();
                e.currentTarget.style.backgroundColor = 'rgba(0, 0, 0, 0.1)';
              }}
              onDragOver={(e) => {
                e.preventDefault();
                e.currentTarget.style.border = '6px dashed #666';
              }}
              onDragLeave={(e) => {
                e.preventDefault();
                e.currentTarget.style.border = '6px dashed #ccc';
                e.currentTarget.style.backgroundColor = 'transparent';
              }}
              onDrop={handleDrop}
              style={{
                display: 'inline-flex',
                width: '140px',
                height: '140px',
                border: '6px dashed #ccc',
                borderRadius: '20px',
                marginLeft: '16px',
                justifyContent: 'center',
                alignItems: 'center',
                transition: 'all 0.2s ease',
                opacity: 0.8
              }}
            >
              {selectedMark && activeDropZone === 'end' && (
                <PunctuationCharacter
                  type={selectedMark}
                  isCorrect={isCorrect}
                  onClick={() => {}}
                />
              )}
            </span>
          )}
        </div>
      </div>
    );
  };

  const handleGameOverClose = () => {
    setGameOverOpen(false);
    navigate('/select');
  };

  const formatTime = (seconds: number | null): string => {
    if (seconds === null) return "0:00";
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const TimerDisplay = () => (
    <Box sx={{ 
      position: 'absolute',
      top: 16,
      right: 16,
      backgroundColor: (!hasStarted || timeLeft === null) ? 'primary.main' :
                      timeLeft < 10 ? 'error.main' : 'primary.main',
      color: 'white',
      borderRadius: '50%',
      width: 80,
      height: 80,
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      transition: 'background-color 0.3s ease',
      boxShadow: 2
    }}>
      <Typography 
        variant="h6" 
        sx={{ 
          fontFamily: 'monospace',
          fontWeight: 'bold'
        }}
      >
        {formatTime(timeLeft)}
      </Typography>
    </Box>
  );

  React.useEffect(() => {
    if (settings.timer) {
      setTimeLeft(settings.timeLimit);
    }
  }, [settings.timer, settings.timeLimit]);

  const TopBar = () => (
    <Box sx={{ 
      display: 'flex', 
      justifyContent: 'space-between', 
      alignItems: 'center',
      padding: '16px',
      width: '100%'
    }}>
      <CustomButton onClick={() => navigate('/select')}>
        DONE
      </CustomButton>
      <Typography sx={{ 
        fontFamily: '"Rethink Sans", Arial, sans-serif',
        fontWeight: 800
      }}>
        CORRECT: {score.correct} INCORRECT: {score.incorrect}
      </Typography>
    </Box>
  );

  const handleDrag = (e: React.DragEvent) => {
    const { clientX, clientY } = e;
    setDragPosition({ x: clientX, y: clientY });

    const newDropZone = calculateDropZone(clientX, clientY);
    if (newDropZone !== activeDropZone) {
      setActiveDropZone(newDropZone);
    }
  };

  const handleDragEnd = () => {
    setDragPosition(null);
    setActiveDropZone(null);
  };

  return (
    <Box sx={{ 
      height: '100vh',
      display: 'flex',
      flexDirection: 'column',
      position: 'relative'
    }}>
      <TopBar />

      <Box sx={{ 
        flex: 1,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        padding: '24px',
        paddingBottom: '120px',
        paddingTop: '12px',
      }}>
        <Box sx={{ 
          flex: 1,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center'
        }}>
          {renderPhrase()}
        </Box>
      </Box>

      <Box sx={{ 
        position: 'fixed',
        bottom: 0,
        left: 0,
        right: 0,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        gap: '32px',
        padding: '24px',
        minHeight: '120px',
        backgroundColor: 'transparent',
        zIndex: 2
      }}>
        {shuffledTypes.map((type) => (
          <PunctuationCharacter
            key={type}
            type={type}
            onClick={() => handleCharacterSelect(type)}
            isCorrect={undefined}
            isDraggable={true}
            onDragStart={() => setSelectedMark(null)}
            onDrag={handleDrag}
            onDragEnd={handleDragEnd}
            onSelect={handleCharacterSelect}
          />
        ))}
      </Box>

      {settings.timer && <TimerDisplay />}
      <GameOverDialog 
        open={gameOverOpen} 
        onClose={handleGameOverClose}
        score={score}
      />
    </Box>
  );
};

export default GameScreen;
