import * as React from 'react';
import { Box } from '@mui/material';

interface PunctuationCharacterProps {
  type: string;
  isCorrect?: boolean | null;
  onClick: () => void;
  isDraggable?: boolean;
  onDragStart?: (e: React.DragEvent) => void;
}

const PunctuationCharacter = ({ 
  type, 
  isCorrect, 
  onClick, 
  isDraggable,
  onDragStart 
}: PunctuationCharacterProps) => {
  const getCharacterImage = () => {
    const baseName = type.toLowerCase();
    if (isCorrect === true) {
      return require(`../assets/characters/${baseName}/${baseName}-happy.png`);
    } else if (isCorrect === false) {
      return require(`../assets/characters/${baseName}/${baseName}-sad.png`);
    }
    return require(`../assets/characters/${baseName}/${baseName}-default.png`);
  };

  const handleDragStart = (e: React.DragEvent<HTMLDivElement>) => {
    e.dataTransfer.setData('application/punctuation', type);
    if (onDragStart) onDragStart(e);
  };

  return (
    <Box
      component="div"
      sx={{
        width: 80,
        height: 80,
        cursor: isDraggable ? 'grab' : 'default',
        transition: 'all 0.3s ease',
        '&:hover': {
          transform: isDraggable ? 'scale(1.1)' : 'none',
        },
        '&:active': {
          cursor: isDraggable ? 'grabbing' : 'default',
        }
      }}
      onClick={onClick}
      draggable={isDraggable}
      onDragStart={handleDragStart}
    >
      <img 
        src={getCharacterImage()} 
        alt={type}
        style={{
          width: '100%',
          height: '100%',
          objectFit: 'contain'
        }}
      />
    </Box>
  );
};

export default PunctuationCharacter;
