import React from 'react';

export type PunctuationType = 'period' | 'exclamation' | 'question' | 'comma';

interface PunctuationCharacterProps {
  type: PunctuationType;
  onClick: () => void;
  isCorrect?: boolean | null;
  isDraggable?: boolean;
  onDragStart?: () => void;
  onSelect?: (type: PunctuationType) => void;
}

const characterImages = {
  period: {
    default: require('../assets/characters/peri/peri-default.png'),
    happy: require('../assets/characters/peri/peri-happy.png'),
    sad: require('../assets/characters/peri/peri-sad.png'),
  },
  exclamation: {
    default: require('../assets/characters/ex/ex-default.png'),
    happy: require('../assets/characters/ex/ex-happy.png'),
    sad: require('../assets/characters/ex/ex-sad.png'),
  },
  question: {
    default: require('../assets/characters/quest/quest-default.png'),
    happy: require('../assets/characters/quest/quest-happy.png'),
    sad: require('../assets/characters/quest/quest-sad.png'),
  },
  comma: {
    default: require('../assets/characters/curly/curly-default.png'),
    happy: require('../assets/characters/curly/curly-happy.png'),
    sad: require('../assets/characters/curly/curly-sad.png'),
  },
} as const;

const PunctuationCharacter: React.FC<PunctuationCharacterProps> = ({
  type,
  onClick,
  isCorrect,
  isDraggable = false,
  onDragStart,
  onSelect,
}) => {
  const getCharacterImage = () => {
    if (isCorrect === true) {
      return characterImages[type].happy;
    } else if (isCorrect === false) {
      return characterImages[type].sad;
    }
    return characterImages[type].default;
  };

  const getCharacterSize = () => {
    switch (type) {
      case 'comma':
        return 'small';
      case 'period':
        return 'small';
      default:
        return 'large';
    }
  };

  const handleDragStart = (e: React.DragEvent) => {
    e.dataTransfer.setData('application/punctuation', type);
    if (onDragStart) {
      onDragStart();
    }
  };

  return (
    <div
      className={`punctuation-character ${getCharacterSize()}`}
      onClick={() => {
        onClick();
        if (onSelect) {
          onSelect(type);
        }
      }}
      draggable={isDraggable}
      onDragStart={handleDragStart}
    >
      <img src={getCharacterImage()} alt={type} />
    </div>
  );
};

export default PunctuationCharacter;
