import * as React from 'react';
import { Box, Typography, Button, Grid } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { useGame } from '../context/GameContext';
import PunctuationCharacter from '../components/PunctuationCharacter';

const samplePhrases = {
  period: [
    { text: "I love to play in the park", answer: "period" },
    { text: "The sun is shining today", answer: "period" },
  ],
  exclamation: [
    { text: "What a wonderful day", answer: "exclamation" },
    { text: "I can't believe it", answer: "exclamation" },
  ],
  question: [
    { text: "How are you today", answer: "question" },
    { text: "Where did you go", answer: "question" },
  ],
  comma: [
    { text: "After the movie we went home", answer: "comma" },
    { text: "Yes I would love to", answer: "comma" },
  ],
};

const GameScreen = () => {
  const navigate = useNavigate();
  const { punctuationType } = useGame();
  const [currentPhrase, setCurrentPhrase] = React.useState<any>(null);
  const [isCorrect, setIsCorrect] = React.useState<boolean | null>(null);

  React.useEffect(() => {
    if (punctuationType && samplePhrases[punctuationType]) {
      const phrases = samplePhrases[punctuationType];
      setCurrentPhrase(phrases[Math.floor(Math.random() * phrases.length)]);
    }
  }, [punctuationType]);

  const handleCharacterSelect = (type: string) => {
    const correct = type === currentPhrase.answer;
    setIsCorrect(correct);

    if (correct) {
      setTimeout(() => {
        const phrases = samplePhrases[punctuationType];
        setCurrentPhrase(phrases[Math.floor(Math.random() * phrases.length)]);
        setIsCorrect(null);
      }, 1500);
    }
  };

  return (
    <Box sx={{ p: 4, textAlign: 'center' }}>
      <Button onClick={() => navigate('/')} sx={{ position: 'absolute', top: 16, left: 16 }}>
        Back to Menu
      </Button>

      {currentPhrase && (
        <>
          <Typography variant="h4" sx={{ mb: 8 }}>
            {currentPhrase.text}
          </Typography>

          <Grid container spacing={2} justifyContent="center">
            {['period', 'exclamation', 'question', 'comma'].map((type) => (
              <Grid item key={type}>
                <PunctuationCharacter
                  type={type}
                  onClick={() => handleCharacterSelect(type)}
                  isCorrect={isCorrect !== null ? type === currentPhrase.answer : undefined}
                />
              </Grid>
            ))}
          </Grid>
        </>
      )}
    </Box>
  );
};

export default GameScreen;
