import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useGame } from '../context/GameContext';
import SettingsModal from '../components/SettingsModal';
import { Button } from '../components/Button';
import '../styles/CharacterSelectScreen.css';

const CharacterSelectScreen = () => {
  const navigate = useNavigate();
  const { setPunctuationType, settings, updateSettings } = useGame();
  const [settingsOpen, setSettingsOpen] = React.useState(false);

  const handleSettingsOpen = () => setSettingsOpen(true);
  const handleSettingsClose = () => setSettingsOpen(false);

  const handleSettingsChange = (newSettings: Partial<typeof settings>) => {
    updateSettings(newSettings);
  };

  const handleSelect = (type: string) => {
    setPunctuationType(type);
    navigate('/game');
  };

  return (
    <div className="character-select-screen">
      <header className="select-header">
        <Button onClick={() => navigate('/')}>
          BACK
        </Button>
        <Button onClick={handleSettingsOpen}>
          SETTINGS
        </Button>
      </header>

      <main className="select-content">
        <h1 className="select-title">SELECT YOUR PUNCTUATION</h1>

        <div className="character-grid">
          <button 
            className="character-button"
            onClick={() => handleSelect('period')}
          >
            <span className="character">.</span>
            <span className="label">PERIOD</span>
          </button>

          <button 
            className="character-button"
            onClick={() => handleSelect('exclamation')}
          >
            <span className="character">!</span>
            <span className="label">EXCLAMATION</span>
          </button>

          <button 
            className="character-button"
            onClick={() => handleSelect('question')}
          >
            <span className="character">?</span>
            <span className="label">QUESTION</span>
          </button>

          <button 
            className="character-button"
            onClick={() => handleSelect('comma')}
          >
            <span className="character">,</span>
            <span className="label">COMMA</span>
          </button>
        </div>
      </main>

      <SettingsModal
        open={settingsOpen}
        onClose={handleSettingsClose}
        settings={settings}
        onSettingsChange={handleSettingsChange}
      />
    </div>
  );
};

export default CharacterSelectScreen;
