import * as React from 'react';
import { 
  Dialog, 
  DialogTitle, 
  DialogContent, 
  DialogActions, 
  Button,
  FormControl,
  FormControlLabel,
  Switch,
  Typography,
  Slider,
  Box
} from '@mui/material';

interface SettingsModalProps {
  open: boolean;
  onClose: () => void;
  settings: {
    sound: boolean;
    difficulty: number;
    timer: boolean;
  };
  onSettingsChange: (settings: any) => void;
}

const SettingsModal = ({ open, onClose, settings, onSettingsChange }: SettingsModalProps) => {
  const handleSoundToggle = () => {
    onSettingsChange({ ...settings, sound: !settings.sound });
  };

  const handleDifficultyChange = (_event: Event, newValue: number | number[]) => {
    onSettingsChange({ ...settings, difficulty: newValue as number });
  };

  const handleTimerToggle = () => {
    onSettingsChange({ ...settings, timer: !settings.timer });
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>Settings</DialogTitle>
      <DialogContent>
        <Box sx={{ minWidth: 300, pt: 2 }}>
          <FormControl fullWidth sx={{ mb: 3 }}>
            <FormControlLabel
              control={<Switch checked={settings.sound} onChange={handleSoundToggle} />}
              label="Sound Effects"
            />
          </FormControl>

          <Box sx={{ mb: 3 }}>
            <Typography gutterBottom>Difficulty</Typography>
            <Slider
              value={settings.difficulty}
              onChange={handleDifficultyChange}
              step={1}
              marks
              min={1}
              max={3}
              valueLabelDisplay="auto"
              valueLabelFormat={(value) => 
                value === 1 ? 'Easy' : value === 2 ? 'Medium' : 'Hard'
              }
            />
          </Box>

          <FormControl fullWidth>
            <FormControlLabel
              control={<Switch checked={settings.timer} onChange={handleTimerToggle} />}
              label="Timer Mode"
            />
          </FormControl>
        </Box>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Close</Button>
      </DialogActions>
    </Dialog>
  );
};

export default SettingsModal;
