import * as React from 'react';
import { 
  Dialog, 
  DialogTitle, 
  DialogContent, 
  DialogActions, 
  Button,
  FormControl,
  FormControlLabel,
  Switch,
  Typography,
  Slider,
  Box,
  Select,
  MenuItem
} from '@mui/material';

interface SettingsModalProps {
  open: boolean;
  onClose: () => void;
  settings: {
    sound: boolean;
    difficulty: number;
    timer: boolean;
    timeLimit: number; // in seconds
  };
  onSettingsChange: (settings: any) => void;
}

const SettingsModal = ({ open, onClose, settings, onSettingsChange }: SettingsModalProps) => {
  const handleTimerToggle = () => {
    onSettingsChange({ ...settings, timer: !settings.timer });
  };

  const handleTimeLimitChange = (event: any) => {
    onSettingsChange({ ...settings, timeLimit: event.target.value });
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>Settings</DialogTitle>
      <DialogContent>
        <Box sx={{ minWidth: 300, pt: 2 }}>
          <FormControl fullWidth sx={{ mb: 3 }}>
            <FormControlLabel
              control={<Switch checked={settings.sound} onChange={() => 
                onSettingsChange({ ...settings, sound: !settings.sound })} 
              />}
              label="Sound Effects"
            />
          </FormControl>

          <Box sx={{ mb: 3 }}>
            <Typography gutterBottom>Difficulty</Typography>
            <Slider
              value={settings.difficulty}
              onChange={(_e, val) => onSettingsChange({ ...settings, difficulty: val })}
              step={1}
              marks
              min={1}
              max={3}
              valueLabelDisplay="auto"
              valueLabelFormat={(value) => 
                value === 1 ? 'Easy' : value === 2 ? 'Medium' : 'Hard'
              }
            />
          </Box>

          <FormControl fullWidth sx={{ mb: 2 }}>
            <FormControlLabel
              control={<Switch checked={settings.timer} onChange={handleTimerToggle} />}
              label="Timer Mode"
            />
          </FormControl>

          {settings.timer && (
            <FormControl fullWidth sx={{ mb: 2 }}>
              <Typography gutterBottom>Time Limit (seconds)</Typography>
              <Select
                value={settings.timeLimit}
                onChange={handleTimeLimitChange}
              >
                <MenuItem value={30}>30 seconds</MenuItem>
                <MenuItem value={60}>1 minute</MenuItem>
                <MenuItem value={120}>2 minutes</MenuItem>
                <MenuItem value={180}>3 minutes</MenuItem>
              </Select>
            </FormControl>
          )}
        </Box>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Close</Button>
      </DialogActions>
    </Dialog>
  );
};

export default SettingsModal;
